export const getProductsQuery = gql`
{
  Product{
    id,
    name,
    price,
    category,
    shortDesc,
    description
  }
}
`;

export const addProductQuery = gql`
  mutation{
    addProduct( name:"", price:"", category:"", shortDesc:"", description:""){
      name
      price
      category
      shortDesc
      description
    }
  }
`;
