import React from 'react'
import { useQuery } from '@apollo/react-hooks';
import { getProductQuery } from '../queries'


export default function ProductDetail({ match }) {
  const id = (match.params)
  const { data, loading, error } = useQuery(getProductQuery)
  if (loading) { return <div>Loading...</div>; }
  if (error) { return <div>{`Error! ${error.message}`}</div>; }
  console.log(id)
  console.log(data.product)
  return (
    <>
      <div className=" text-center mt-2">
        <img src="https://via.placeholder.com/500" width="300" height="500" alt="item" />
      </div>
      <div>
        <h5 className="text-center"></h5>
        <h4 className="text-center"></h4>
        <h4>Description</h4>
        <p ></p>
      </div>
      <a href="/" className="btn btn-primary rounded mb-2">Add to cart</a>
    </>
  )
}
