import React, { createContext, useReducer } from 'react'
import { globalReducer } from './globaleReducer'

const initialState = {
  products: [
    { id: 1, name: "Samsung Galaxy A71", price: 550, category: 'Electronics', description: "Some quick example text to build on the card title" },
    { id: 2, name: "Samsung Galaxy Galaxy", price: 400, category: 'Electronics', description: "Some quick example text to build on the card title" },
    { id: 3, name: "Samsung Galaxy N71", price: 300, category: 'Electronics', description: "Some quick example text to build on the card title" },
    { id: 4, name: "Samsung Galaxy O70", price: 5500, category: 'Electronics', description: "Some quick example text to build on the card title" },
    { id: 5, name: "Samsung Galaxy A61", price: 1500, category: 'Electronics', description: "Some quick example text to build on the card title" }
  ]
}
export const GlobalContext = createContext(initialState)

export default function GobaleStateProvider({ children }) {
  const [state, dispatch] = useReducer(globalReducer, initialState)

  //Actions
  const addProduct = (newProduct) => {
    dispatch({
      type: 'ADD_PRODUCT',
      payload: newProduct
    })
  }

  return (
    <GlobalContext.Provider value={{
      products: state.products,
      addProduct
    }}>
      {children}
    </GlobalContext.Provider >
  )
}
