import React, { useContext, useReducer } from 'react'
import { globalReducer } from './globaleReducer'

const initialState = {
  prodacts: [
    { id: 1, item: "Samsung Galaxy A71", price: 550, description: "Some quick example text to build on the card title" },
    { id: 2, item: "Samsung Galaxy Galaxy", price: 400, description: "Some quick example text to build on the card title" },
    { id: 3, item: "Samsung Galaxy N71", price: 300, description: "Some quick example text to build on the card title" },
    { id: 4, item: "Samsung Galaxy O70", price: 5500, description: "Some quick example text to build on the card title" },
    { id: 5, item: "Samsung Galaxy A61", price: 1500, description: "Some quick example text to build on the card title" }
  ]
}
export const GlobalContext = useContext(initialState)

export default function GobaleStateProvider({ children }) {
  const [state, dispatch] = useReducer(globalReducer, initialState)

  //Actions

  return (
    <GloblalContext.Provider value={{ products: state.products }}>
      {children}
    </GloblalContext.Provider >
  )
}
