import React, { createContext, useReducer } from 'react'
import { globalReducer } from './globaleReducer'
import { gql } from 'apollo-boost'
import { useQuery } from '@apollo/react-hooks';

const initialState = {
  products: [
    { id: 1, name: "Samsung Galaxy A71", price: 550, category: 'Electronics', shortDesc: "Some quick example text to build on the card title" },
    { id: 2, name: "Samsung Galaxy Galaxy", price: 400, category: 'Health', shortDesc: "Some quick example text to build on the card title" },
    { id: 3, name: "Samsung Galaxy N71", price: 300, category: 'Toys', shortDesc: "Some quick example text to build on the card title" },
    { id: 4, name: "Samsung Galaxy O70", price: 5500, category: 'Electronics', shortDesc: "Some quick example text to build on the card title" },
    { id: 5, name: "Samsung Galaxy A61", price: 1500, category: 'Kitchen', shortDesc: "Some quick example text to build on the card title" }
  ]
}

const getProductsQuery = gql`
{
