import React, { useState, useContext } from 'react'
//import { GlobalContext } from '../context/GlobalContext'
import { useMutation } from '@apollo/react-hooks';
import gql from 'graphql-tag'

//import { addProductQuery } from '../queries'


const addProductQuery = gql`
  mutation AddProduct( $name:String!, $price:Number!, $category:String!, $shortDesc:String!, $description:String!){
    addProduct(name:$name, price:$price, category:$category, shortDesc:$shortDesc, description:$description ){
      name
      price
      category
      shortDesc
      description
    }
  }
`;




export default function AddProduct() {
  //const { addProduct } = useContext(GlobalContext)
  const [name, setName] = useState('')
  const [price, setPrice] = useState(0)
  const [category, setCategory] = useState('Health')
  const [shortDesc, setShortDesc] = useState('')
  const [description, setDescription] = useState('')
  const [addProduct, { data }] = useMutation(addProductQuery, { variables: { name, price, category, shortDesc, description } });
  const handleSubmit = (e) => {
    e.preventDefault()
    const newProduct = { name, price, category, shortDesc, description }
    addProduct()
    setName('')
    setPrice(0)
    setCategory('Health')
    setShortDesc('')
    setDescription('')
  }

  return (
    <form className="py-5" onSubmit={handleSubmit}>
      <div className="form-group">
        <label htmlFor="name">Product name</label>
        <input type="text" className="form-control" id="name" value={name} onChange={(e) => { setName(e.target.value) }} />
      </div>

      <div className="form-row">

        <div className="form-group col-md-4">
          <label htmlFor="price">Product price</label>
          <input type="number" className="form-control" id="price" value={price} onChange={(e) => { setPrice(e.target.value) }} />
        </div>

        <div className="form-group col-md-4">
          <label htmlFor="category">Product category</label>
          <select id="category" className="form-control" value={category} onChange={(e) => { setCategory(e.target.value) }}>
            <option>Health</option>
            <option>Kitchen</option>
            <option>Toys</option>
            <option>Electronics</option>
            <option>Clothing</option>
          </select>
        </div>

        <div className="form-group col-md-4">
          <label htmlFor="img">Product image</label>
          <input type="file" className="form-control-file" id="img" />
        </div>

      </div>

      <div className="form-group">
        <label htmlFor="shortDesc">Product short Description</label>
        <input type="text" className="form-control" id="shortDesc" value={shortDesc} onChange={(e) => { setShortDesc(e.target.value) }} />
      </div>

      <div className="form-group">
        <label htmlFor="fullDesc">Product full description</label>
        <textarea type="text" className="form-control" rows="10" id="fullDesc" value={description} onChange={(e) => { setDescription(e.target.value) }}></textarea>
      </div>

      <button type="submit" className="btn btn-primary">Submit</button>
    </form>
  )
}
