import React from 'react'
import { useQuery } from '@apollo/react-hooks';
import { getProductsQuery } from '../queries'


export default function ProductDetail(props) {
  const { data, loading, error } = useQuery(getProductsQuery)
  if (loading) { return <div>Loading...</div>; }
  if (error) { return <div>{`Error! ${error.message}`}</div>; }
  const selectedProduct = data.products.filter((product) => product.id === '5ebd2e596b869534ec93707c');
  console.log(selectedProduct)

  return (
    <>
      <div className=" text-center mt-2">
        <img src="https://via.placeholder.com/500" width="300" height="500" alt="item" />
      </div>
      <div>
        <h5 className="text-center">{selectedProduct.name}</h5>
        <h4 className="text-center">{selectedProduct.price}$</h4>
        <h4>Description</h4>
        <p >{selectedProduct.description}</p>
      </div>
      <a href="/" className="btn btn-primary rounded mb-2">Add to cart</a>
    </>
  )
}
